    projectId: process.env.NUXT_PUBLIC_APPWRITE_PROJECT_ID || '',
  },
  runtimeConfig: {
    public: {
      appwriteEndpoint: process.env.NUXT_PUBLIC_APPWRITE_ENDPOINT,
      appwriteProjectId: process.env.NUXT_PUBLIC_APPWRITE_PROJECT_ID,
    },
  },
});
```

## Authentication Implementation

Create authentication composable (composables/useAuth.ts):
```typescript
import { ID } from 'appwrite';

export const useAuth = () => {
  const { $appwrite } = useNuxtApp();
  const user = useState('user', () => null);
  const loading = useState('loading', () => true);

  const checkAuth = async () => {
    try {
      const account = await $appwrite.account.get();
      user.value = account;
      return account;
    } catch (error) {
      user.value = null;
      console.error('Check auth error:', error);
    } finally {
      loading.value = false;
    }
  };

  const login = async (email: string, password: string) => {
    try {
      await $appwrite.account.createEmailSession(email, password);
      return checkAuth();
    } catch (error) {
      console.error('Login error:', error);
      throw error;
    }
  };

  const register = async (email: string, password: string, name: string) => {
    try {
      await $appwrite.account.create(ID.unique(), email, password, name);
      return login(email, password);
    } catch (error) {
      console.error('Register error:', error);
      throw error;
    }
  };

  const logout = async () => {
    try {
      await $appwrite.account.deleteSession('current');
      user.value = null;
    } catch (error) {
      console.error('Logout error:', error);
      throw error;
    }
  };

  return {
    user,
    loading,
    checkAuth,
    login,
    register,
    logout,
  };
};
```

## Database Implementation

Create database composable (composables/useDatabase.ts):
```typescript
import { ID, Query } from 'appwrite';

export const useDatabase = (databaseId: string, collectionId: string) => {
  const { $appwrite } = useNuxtApp();
  const loading = useState('dbLoading', () => false);
  const error = useState('dbError', () => null);

  const listDocuments = async (queries: any[] = []) => {
    loading.value = true;
    error.value = null;
    try {
      return await $appwrite.databases.listDocuments(
        databaseId,
        collectionId,
        [
          Query.limit(20),
          Query.orderDesc('$createdAt'),
          ...queries,
        ]
      );
    } catch (err) {
      error.value = err;
      throw err;
    } finally {
      loading.value = false;
    }
  };

  const getDocument = async (documentId: string) => {
    loading.value = true;
    error.value = null;
    try {
      return await $appwrite.databases.getDocument(
        databaseId,
        collectionId,
        documentId
      );
    } catch (err) {
      error.value = err;
      throw err;
    } finally {
      loading.value = false;
    }
  };

  const createDocument = async (data: any) => {
    loading.value = true;
    error.value = null;
    try {
      return await $appwrite.databases.createDocument(
        databaseId,
        collectionId,
        ID.unique(),
        data
      );
    } catch (err) {
      error.value = err;
      throw err;
    } finally {
      loading.value = false;
    }
  };

  const updateDocument = async (documentId: string, data: any) => {
    loading.value = true;
    error.value = null;
    try {
      return await $appwrite.databases.updateDocument(
        databaseId,
        collectionId,
        documentId,
        data
      );
    } catch (err) {
      error.value = err;
      throw err;
    } finally {
      loading.value = false;
    }
  };

  const deleteDocument = async (documentId: string) => {
    loading.value = true;
    error.value = null;
    try {
      await $appwrite.databases.deleteDocument(
        databaseId,
        collectionId,
        documentId
      );
    } catch (err) {
      error.value = err;
      throw err;
    } finally {
      loading.value = false;
    }
  };

  return {
    loading,
    error,
    listDocuments,
    getDocument,
    createDocument,
    updateDocument,
    deleteDocument,
  };
};
```

