// js/database.js
class Database {
    constructor() {
        this.db = window.db;
        this.setupUI();
    }

    setupUI() {
        const addForm = document.getElementById('addForm');
        addForm?.addEventListener('submit', async (e) => {
            e.preventDefault();
            const data = {
                title: e.target.title.value,
                content: e.target.content.value,
                createdAt: new Date()
            };

            try {
                await this.addDocument('posts', data);
                addForm.reset();
                this.loadDocuments();
            } catch (error) {
                console.error('Failed to add document:', error);
                this.showError(error.message);
            }
        });

        // Initial load
        this.loadDocuments();
    }

    async addDocument(collection, data) {
        try {
            const docRef = await this.db.collection(collection).add(data);
            console.log('Document written with ID:', docRef.id);
            return docRef;
        } catch (error) {
            console.error('Error adding document:', error);
            throw error;
        }
    }

    async getDocuments(collection, queries = []) {
        try {
            let ref = this.db.collection(collection);
            
            // Apply queries if any
            queries.forEach(query => {
                ref = ref.where(query.field, query.operator, query.value);
            });

            const snapshot = await ref.get();
            return snapshot.docs.map(doc => ({
                id: doc.id,
                ...doc.data()
            }));
        } catch (error) {
            console.error('Error getting documents:', error);
            throw error;
        }
    }

    async updateDocument(collection, id, data) {
        try {
            await this.db.collection(collection).doc(id).update(data);
            console.log('Document updated:', id);
        } catch (error) {
            console.error('Error updating document:', error);
            throw error;
        }
    }

    async deleteDocument(collection, id) {
        try {
            await this.db.collection(collection).doc(id).delete();
            console.log('Document deleted:', id);
        } catch (error) {
            console.error('Error deleting document:', error);
            throw error;
        }
    }

    async loadDocuments() {
        try {
            const posts = await this.getDocuments('posts');
            this.renderDocuments(posts);
        } catch (error) {
            console.error('Error loading documents:', error);
            this.showError(error.message);
        }
    }

    renderDocuments(documents) {
        const container = document.getElementById('posts');
        if (!container) return;

        container.innerHTML = documents.map(doc => `
            <div class="post" data-id="${doc.id}">
                <h3>${doc.title}</h3>
                <p>${doc.content}</p>
                <button onclick="db.deleteDocument('posts', '${doc.id}')">Delete</button>
            </div>
        `).join('');
    }

    showError(message) {
        const errorDiv = document.getElementById('error');
        if (errorDiv) {
            errorDiv.textContent = message;
            errorDiv.classList.remove('hidden');
            setTimeout(() => {
                errorDiv.classList.add('hidden');
            }, 5000);
        }
    }
}

