## Authentication Setup

1. Create authentication hooks in `lib/hooks/useAuth.ts`:
```typescript
'use client';

import { useEffect, useState } from 'react';
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signInWithPopup,
  GoogleAuthProvider,
  signOut,
  onAuthStateChanged,
  type User
} from 'firebase/auth';
import { auth } from '@/lib/firebase/client';
import { useRouter } from 'next/navigation';

export function useAuth() {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const router = useRouter();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
      setLoading(false);
    });

    return () => unsubscribe();
  }, []);

  const signIn = async (email: string, password: string) => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      router.push('/dashboard');
    } catch (error: any) {
      throw new Error(error.message);
    }
  };

  const signUp = async (email: string, password: string) => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      router.push('/dashboard');
    } catch (error: any) {
      throw new Error(error.message);
    }
  };

  const signInWithGoogle = async () => {
    try {
      const provider = new GoogleAuthProvider();
      await signInWithPopup(auth, provider);
      router.push('/dashboard');
    } catch (error: any) {
      throw new Error(error.message);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      router.push('/');
    } catch (error: any) {
      throw new Error(error.message);
    }
  };

  return {
    user,
    loading,
    signIn,
    signUp,
    signInWithGoogle,
    logout
  };
}
```

2. Create authentication middleware in `middleware.ts`:
```typescript
import { NextResponse } from 'next/server';
import type { NextRequest } from 'next/server';
import { adminAuth } from '@/lib/firebase/admin';

export async function middleware(request: NextRequest) {
  const session = request.cookies.get('session')?.value;

  // Return to /login if no session exists
  if (!session) {
    return NextResponse.redirect(new URL('/login', request.url));
  }

  try {
    // Verify session
    const decodedClaim = await adminAuth.verifySessionCookie(session, true);

    // Add user to request
    const requestHeaders = new Headers(request.headers);
    requestHeaders.set('user', JSON.stringify(decodedClaim));

    // Return request with user
    return NextResponse.next({
      request: {
        headers: requestHeaders,
      },
    });
  } catch (error) {
    // Delete session cookie if invalid
    const response = NextResponse.redirect(new URL('/login', request.url));
    response.cookies.delete('session');
    return response;
  }
}

export const config = {
  matcher: ['/dashboard/:path*']
};
```

## Firestore Setup

1. Create Firestore hooks in `lib/hooks/useFirestore.ts`:
```typescript
'use client';

import { useState, useCallback } from 'react';
import {
  collection,
  doc,
  query,
  where,
  orderBy,
  limit,
  getDocs,
  getDoc,
  addDoc,
  updateDoc,
  deleteDoc,
  type Query,
  type DocumentData
} from 'firebase/firestore';
import { db } from '@/lib/firebase/client';

export function useFirestore<T = DocumentData>(collectionName: string) {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<Error | null>(null);

  const getDocuments = useCallback(async (
    constraints?: {
      where?: [string, firebase.firestore.WhereFilterOp, any][];
      orderBy?: [string, 'asc' | 'desc'][];
      limit?: number;
    }
  ) => {
    setLoading(true);
    setError(null);

    try {
      let q: Query = collection(db, collectionName);

      if (constraints) {
        if (constraints.where) {
          constraints.where.forEach(([field, op, value]) => {
            q = query(q, where(field, op, value));
          });
        }

        if (constraints.orderBy) {
          constraints.orderBy.forEach(([field, direction]) => {
            q = query(q, orderBy(field, direction));
          });
        }

        if (constraints.limit) {
          q = query(q, limit(constraints.limit));
        }
      }

      const snapshot = await getDocs(q);
      return snapshot.docs.map(doc => ({
        id: doc.id,
        ...doc.data()
      })) as T[];
    } catch (err: any) {
      setError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [collectionName]);

  const getDocument = useCallback(async (id: string) => {
    setLoading(true);
    setError(null);

    try {
      const docRef = doc(db, collectionName, id);
      const docSnap = await getDoc(docRef);

      if (!docSnap.exists()) {
        throw new Error('Document not found');
      }

      return {
        id: docSnap.id,
        ...docSnap.data()
      } as T;
    } catch (err: any) {
      setError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [collectionName]);

  const addDocument = useCallback(async (data: Omit<T, 'id'>) => {
    setLoading(true);
    setError(null);

    try {
      const docRef = await addDoc(collection(db, collectionName), data);
      return docRef.id;
    } catch (err: any) {
      setError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [collectionName]);

  const updateDocument = useCallback(async (id: string, data: Partial<T>) => {
    setLoading(true);
    setError(null);

    try {
      const docRef = doc(db, collectionName, id);
      await updateDoc(docRef, data as DocumentData);
    } catch (err: any) {
      setError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [collectionName]);

  const deleteDocument = useCallback(async (id: string) => {
    setLoading(true);
    setError(null);

    try {
      const docRef = doc(db, collectionName, id);
      await deleteDoc(docRef);
    } catch (err: any) {
      setError(err);
      throw err;
    } finally {
      setLoading(false);
    }
  }, [collectionName]);

  return {
    loading,
    error,
    getDocuments,
    getDocument,
    addDocument,
    updateDocument,
    deleteDocument
  };
}
```

## Server Actions

1. Create server actions in `app/actions/firebase.ts`:
```typescript
'use server';

import { adminDb, adminAuth } from '@/lib/firebase/admin';
import { cookies } from 'next/headers';
import { redirect } from 'next/navigation';

export async function createSessionCookie(idToken: string) {
  try {
    const expiresIn = 60 * 60 * 24 * 5 * 1000; // 5 days
    const sessionCookie = await adminAuth.createSessionCookie(idToken, { expiresIn });
    cookies().set('session', sessionCookie, {
      maxAge: expiresIn,
      httpOnly: true,
      secure: true
    });
  } catch (error) {
    throw new Error('Failed to create session');
  }
}

export async function getUserData(userId: string) {
  try {
    const userDoc = await adminDb.collection('users').doc(userId).get();
    if (!userDoc.exists) {
      throw new Error('User not found');
    }
    return {
      id: userDoc.id,
      ...userDoc.data()
    };
  } catch (error) {
    throw new Error('Failed to get user data');
  }
}

export async function updateUserData(userId: string, data: any) {
  try {
    await adminDb.collection('users').doc(userId).update(data);
  } catch (error) {
    throw new Error('Failed to update user data');
  }
}
```

