## Usage Examples

### Authentication Composable

1. Create `composables/useFirebaseAuth.ts`:
```typescript
import { useFirebaseAuth, useCurrentUser } from 'vuefire'
import {
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  signInWithPopup,
  GoogleAuthProvider,
  signOut as firebaseSignOut,
  onAuthStateChanged,
  type User
} from 'firebase/auth'

export function useAuth() {
  const auth = useFirebaseAuth()!
  const user = useCurrentUser()
  const router = useRouter()
  const loading = ref(false)
  const error = ref<string | null>(null)

  // Email/Password Sign In
  const signInWithEmail = async (email: string, password: string) => {
    loading.value = true
    error.value = null
    try {
      await signInWithEmailAndPassword(auth, email, password)
      router.push('/dashboard')
    } catch (e: any) {
      error.value = e.message
    } finally {
      loading.value = false
    }
  }

  // Email/Password Sign Up
  const signUpWithEmail = async (email: string, password: string) => {
    loading.value = true
    error.value = null
    try {
      await createUserWithEmailAndPassword(auth, email, password)
      router.push('/dashboard')
    } catch (e: any) {
      error.value = e.message
    } finally {
      loading.value = false
    }
  }

  // Google Sign In
  const signInWithGoogle = async () => {
    loading.value = true
    error.value = null
    try {
      const provider = new GoogleAuthProvider()
      await signInWithPopup(auth, provider)
      router.push('/dashboard')
    } catch (e: any) {
      error.value = e.message
    } finally {
      loading.value = false
    }
  }

  // Sign Out
  const signOut = async () => {
    loading.value = true
    error.value = null
    try {
      await firebaseSignOut(auth)
      router.push('/')
    } catch (e: any) {
      error.value = e.message
    } finally {
      loading.value = false
    }
  }

  return {
    user,
    loading,
    error,
    signInWithEmail,
    signUpWithEmail,
    signInWithGoogle,
    signOut
  }
}
```

### Firestore Composables

1. Create `composables/useFirestore.ts`:
```typescript
import { useFirestore, useCollection, useDocument } from 'vuefire'
import {
  collection,
  doc,
  query,
  where,
  orderBy,
  limit,
  addDoc,
  updateDoc,
  deleteDoc,
  type DocumentData,
  type QueryConstraint
} from 'firebase/firestore'

export function useFirestoreCollection<T = DocumentData>(
  path: string,
  constraints?: {
    where?: [string, firebase.firestore.WhereFilterOp, any][];
    orderBy?: [string, 'asc' | 'desc'][];
    limit?: number;
  }
) {
  const db = useFirestore()
  const loading = ref(false)
  const error = ref<string | null>(null)

  const buildQuery = () => {
    let collectionRef = collection(db, path)
    const queryConstraints: QueryConstraint[] = []

    if (constraints) {
      if (constraints.where) {
        constraints.where.forEach(([field, op, value]) => {
          queryConstraints.push(where(field, op, value))
        })
      }

      if (constraints.orderBy) {
        constraints.orderBy.forEach(([field, direction]) => {
          queryConstraints.push(orderBy(field, direction))
        })
      }

      if (constraints.limit) {
        queryConstraints.push(limit(constraints.limit))
      }
    }

    return queryConstraints.length > 0
      ? query(collectionRef, ...queryConstraints)
      : collectionRef
  }

  const data = useCollection<T>(buildQuery())

  const add = async (document: Omit<T, 'id'>) => {
    loading.value = true
    error.value = null
    try {
      const docRef = await addDoc(collection(db, path), document)
      return docRef.id
    } catch (e: any) {
      error.value = e.message
      throw e
    } finally {
      loading.value = false
    }
  }

  return {
    data,
    loading,
    error,
    add
  }
}

export function useFirestoreDocument<T = DocumentData>(path: string, id: string) {
  const db = useFirestore()
  const loading = ref(false)
  const error = ref<string | null>(null)

  const docRef = doc(db, path, id)
  const data = useDocument<T>(docRef)

  const update = async (updates: Partial<T>) => {
    loading.value = true
    error.value = null
    try {
      await updateDoc(docRef, updates as DocumentData)
    } catch (e: any) {
      error.value = e.message
      throw e
    } finally {
      loading.value = false
    }
  }

  const remove = async () => {
    loading.value = true
    error.value = null
    try {
      await deleteDoc(docRef)
    } catch (e: any) {
      error.value = e.message
      throw e
    } finally {
      loading.value = false
    }
  }

  return {
    data,
    loading,
    error,
    update,
    remove
  }
}
```

### Storage Composable

1. Create `composables/useStorage.ts`:
```typescript
import { useFirebaseStorage } from 'vuefire'
import {
  ref as storageRef,
  uploadBytes,
  getDownloadURL,
  deleteObject
} from 'firebase/storage'

export function useStorage(path: string) {
  const storage = useFirebaseStorage()!
  const loading = ref(false)
  const error = ref<string | null>(null)
  const downloadURL = ref<string | null>(null)

  const upload = async (file: File) => {
    loading.value = true
    error.value = null
    try {
      const fileRef = storageRef(storage, `${path}/${file.name}`)
      await uploadBytes(fileRef, file)
      downloadURL.value = await getDownloadURL(fileRef)
      return downloadURL.value
    } catch (e: any) {
      error.value = e.message
      throw e
    } finally {
      loading.value = false
    }
  }

  const remove = async (filename: string) => {
    loading.value = true
    error.value = null
    try {
      const fileRef = storageRef(storage, `${path}/${filename}`)
      await deleteObject(fileRef)
      downloadURL.value = null
    } catch (e: any) {
      error.value = e.message
      throw e
    } finally {
      loading.value = false
    }
  }

  return {
    loading,
    error,
    downloadURL,
    upload,
    remove
  }
}
```

