## Usage Examples

### Basic Database Operations Component
```vue
<script setup lang="ts">
import { ref, onMounted } from 'vue';
import { sql } from '@/lib/db';

interface User {
  id: number;
  name: string;
  email: string;
}

const users = ref<User[]>([]);
const loading = ref(false);
const error = ref<string | null>(null);

const fetchUsers = async () => {
  loading.value = true;
  error.value = null;
  try {
    users.value = await sql<User[]>`
      SELECT id, name, email
      FROM users
      ORDER BY name ASC
    `;
  } catch (e: any) {
    error.value = e.message;
  } finally {
    loading.value = false;
  }
};

const addUser = async (name: string, email: string) => {
  loading.value = true;
  error.value = null;
  try {
    const [newUser] = await sql<User[]>`
      INSERT INTO users (name, email)
      VALUES (${name}, ${email})
      RETURNING id, name, email
    `;
    users.value.push(newUser);
  } catch (e: any) {
    error.value = e.message;
  } finally {
    loading.value = false;
  }
};

const updateUser = async (id: number, name: string, email: string) => {
  loading.value = true;
  error.value = null;
  try {
    const [updatedUser] = await sql<User[]>`
      UPDATE users
      SET name = ${name}, email = ${email}
      WHERE id = ${id}
      RETURNING id, name, email
    `;
    const index = users.value.findIndex(u => u.id === id);
    if (index !== -1) {
      users.value[index] = updatedUser;
    }
  } catch (e: any) {
    error.value = e.message;
  } finally {
    loading.value = false;
  }
};

const deleteUser = async (id: number) => {
  loading.value = true;
  error.value = null;
  try {
    await sql`
      DELETE FROM users
      WHERE id = ${id}
    `;
    users.value = users.value.filter(u => u.id !== id);
  } catch (e: any) {
    error.value = e.message;
  } finally {
    loading.value = false;
  }
};

onMounted(fetchUsers);
</script>

<template>
  <div>
    <h2>Users</h2>
    
    <div v-if="loading">Loading...</div>
    <div v-else-if="error" class="error">{{ error }}</div>
    
    <ul v-else>
      <li v-for="user in users" :key="user.id">
        {{ user.name }} ({{ user.email }})
        <button @click="deleteUser(user.id)">Delete</button>
      </li>
    </ul>
  </div>
</template>
```

### Composable for Database Operations
```typescript
// composables/useDatabase.ts
import { ref } from 'vue';
import { sql } from '@/lib/db';

export function useDatabase<T>() {
  const data = ref<T[]>([]);
  const loading = ref(false);
  const error = ref<string | null>(null);

  const execute = async (query: string, params: any[] = []) => {
    loading.value = true;
    error.value = null;
    try {
      const result = await sql.raw(query, params);
      return result;
    } catch (e: any) {
      error.value = e.message;
      throw e;
    } finally {
      loading.value = false;
    }
  };

  const fetchAll = async (table: string, orderBy?: string) => {
    loading.value = true;
    error.value = null;
    try {
      data.value = await sql<T[]>`
        SELECT *
        FROM ${sql(table)}
        ${orderBy ? sql`ORDER BY ${sql(orderBy)}` : sql``}
      `;
    } catch (e: any) {
      error.value = e.message;
    } finally {
      loading.value = false;
    }
  };

  const fetchById = async (table: string, id: number) => {
    loading.value = true;
    error.value = null;
    try {
      const [result] = await sql<T[]>`
        SELECT *
        FROM ${sql(table)}
        WHERE id = ${id}
      `;
      return result;
    } catch (e: any) {
      error.value = e.message;
    } finally {
      loading.value = false;
    }
  };

  const insert = async (table: string, data: Partial<T>) => {
    loading.value = true;
    error.value = null;
    try {
      const columns = Object.keys(data);
      const values = Object.values(data);
      const [result] = await sql<T[]>`
        INSERT INTO ${sql(table)} (${sql(columns.join(', '))})
        VALUES (${sql(...values)})
        RETURNING *
      `;
      return result;
    } catch (e: any) {
      error.value = e.message;
      throw e;
    } finally {
      loading.value = false;
    }
  };

  const update = async (table: string, id: number, data: Partial<T>) => {
    loading.value = true;
    error.value = null;
    try {
      const setClause = Object.entries(data)
        .map(([key, value]) => `${key} = ${sql`${value}`}`)
        .join(', ');
      
      const [result] = await sql<T[]>`
        UPDATE ${sql(table)}
        SET ${sql(setClause)}
        WHERE id = ${id}
        RETURNING *
      `;
      return result;
    } catch (e: any) {
      error.value = e.message;
      throw e;
    } finally {
      loading.value = false;
    }
  };

  const remove = async (table: string, id: number) => {
    loading.value = true;
    error.value = null;
    try {
      await sql`
        DELETE FROM ${sql(table)}
        WHERE id = ${id}
      `;
    } catch (e: any) {
      error.value = e.message;
      throw e;
    } finally {
      loading.value = false;
    }
  };

  return {
    data,
    loading,
    error,
    execute,
    fetchAll,
    fetchById,
    insert,
    update,
    remove
  };
}
```

