        data,
        include: {
          posts: true
        }
      })
    } catch (error) {
      console.error('Failed to create user:', error)
      throw error
    }
  },

  async updateUser(id, data) {
    try {
      return await prisma.user.update({
        where: { id },
        data,
        include: {
          posts: true
        }
      })
    } catch (error) {
      console.error(`Failed to update user ${id}:`, error)
      throw error
    }
  },

