// Set up real-time subscription
function subscribeToChanges(tableName, callback) {
  try {
    const subscription = supabase
      .channel(`${tableName}_changes`)
      .on('postgres_changes', {
        event: '*',
        schema: 'public',
        table: tableName
      }, (payload) => {
        callback(payload);
      })
      .subscribe();
    
    return subscription;
  } catch (error) {
    console.error('Subscription error:', error);
    throw error;
  }
}

// Example usage
