    case 'INSERT':
      console.log('New post:', payload.new);
      break;
    case 'UPDATE':
      console.log('Updated post:', payload.new);
      break;
    case 'DELETE':
      console.log('Deleted post:', payload.old);
      break;
  }
});

// Clean up subscription
function cleanup() {
  subscription.unsubscribe();
}
