## CORRECT AUTH STORE IMPLEMENTATION

```typescript
// src/lib/stores/supabase.ts
import { createClient, type SupabaseClient, type User } from '@supabase/supabase-js'
import { writable, derived } from 'svelte/store'
import type { Writable } from 'svelte/store'

interface SupabaseState {
  client: SupabaseClient
  user: User | null
  loading: boolean
  error: Error | null
}

const createSupabaseStore = () => {
  const supabase = createClient(
    import.meta.env.VITE_SUPABASE_URL,
    import.meta.env.VITE_SUPABASE_ANON_KEY
  )

  const { subscribe, set, update }: Writable<SupabaseState> = writable({
    client: supabase,
    user: null,
    loading: true,
    error: null
  })

  async function initAuth() {
    update(state => ({ ...state, loading: true }))
    try {
      const { data: { session }, error } = await supabase.auth.getSession()
      if (error) throw error

      const { data: { user } } = await supabase.auth.getUser()
      
      set({
        client: supabase,
        user,
        loading: false,
        error: null
      })

      // Set up auth state listener
      supabase.auth.onAuthStateChange((_event, session) => {
        update(state => ({
          ...state,
          user: session?.user ?? null
        }))
      })
    } catch (error) {
      update(state => ({
        ...state,
        error: error as Error,
        loading: false
      }))
    }
  }

  async function signIn(email: string, password: string) {
    update(state => ({ ...state, loading: true }))
    try {
      const { error } = await supabase.auth.signInWithPassword({
        email,
        password
      })
      if (error) throw error
      update(state => ({ ...state, loading: false, error: null }))
    } catch (error) {
      update(state => ({
        ...state,
        error: error as Error,
        loading: false
      }))
    }
  }

  async function signUp(email: string, password: string) {
    update(state => ({ ...state, loading: true }))
    try {
      const { error } = await supabase.auth.signUp({
        email,
        password
      })
      if (error) throw error
      update(state => ({ ...state, loading: false, error: null }))
    } catch (error) {
      update(state => ({
        ...state,
        error: error as Error,
        loading: false
      }))
    }
  }

  async function signOut() {
    update(state => ({ ...state, loading: true }))
    try {
      const { error } = await supabase.auth.signOut()
      if (error) throw error
      update(state => ({ ...state, loading: false, error: null }))
    } catch (error) {
      update(state => ({
        ...state,
        error: error as Error,
        loading: false
      }))
    }
  }

  return {
    subscribe,
    signIn,
    signUp,
    signOut,
    initAuth
  }
}

export const supabase = createSupabaseStore()
export const user = derived(supabase, $supabase => $supabase.user)
export const loading = derived(supabase, $supabase => $supabase.loading)
export const error = derived(supabase, $supabase => $supabase.error)
```

