  let { product } = $props<{
    product: Product
  }>();
  
  let isHovered = $state(false);
  let buttonClasses = $derived(() => ({
    'btn-primary': true,
    'btn-hover': isHovered
  }));
</script>

<div class="product-card">
  <h3>{product.title}</h3>
  <button class={Object.entries(buttonClasses)
    .filter(([, value]) => value)
    .map(([key]) => key)
    .join(' ')}
