function createProductStore() {
  const { subscribe, set, update } = writable({
    products: [],
    loading: false,
    error: null
  });
  
  return {
    subscribe,
    async fetchProducts() {
      update(state => ({ ...state, loading: true }));
      // Fetch implementation
    }
